mapper.update:
update board
set
board_reply_seq = board_reply_seq + 1
where board_ref = #{boardRef}
and
board_reply_lev = #{boardReplyLev}
and board_reply_ref =
#{boardReplyRef}

update board
set
board_title = #{boardTitle},
board_content = #{boardContent}
where
board_num = #{boardNum}

choose.otherwise:
update board
set board_title = #{boardTitle},
board_content
= #{boardContent}
where board_num = #{boardNum}
